condition.expression:
if (views != global_yt_autohd_views){
    global_yt_autohd_views = views;
    return true
}
return false;

action.script:
_views = "com.google.android.apps.youtube.gaming:id/num_views";
if (existsElementById(_views)){
  views = getTextById(_views);
  if (views != global_yt_autohd_views){
    global_yt_autohd_views = views;
  }
}

resolutions = newList("1440p", "1080p60", "1080p", "720p60", "720p", "480p", "360p", "240p", "144p");
for (res in resolutions) {
  text = getText(res);
  if (text != null){
    click(res);
    return;
  }
}

text = getText("1080p60");
if (text != null){
  click("1080p60");
  return;
}
text = getText("1080p");
if (text != null){
  click("1080p");
  return;
}
text = getText("720p60");
if (text != null){
  click("720p60");
  return;
}
text = getText("720p");
if (text != null){
  click("720p");
  return;
}
text = getText("480p");
if (text != null){
  click("480p");
  return;
}
text = getText("360p");
if (text != null){
  click("360p");
  return;
}
